        /// <summary>
        /// This endpoint is used to get Company Transfer Trade Rules
        /// </summary>
        /// <response code="200">
        /// 
        /// Example Value | Schema :
        ///     
        ///    "{\"IsTIFChange\":true,\"IsTradingAccChange\":true,\"IsAccountChange\":true,\"IsStrategyChange\":false,\"IsHandlingInstrChange\":true,\"IsVenueCPChange\":true,\"IsAllowAllUserToCancelReplaceRemove\":true,\"IsAllowUserToChangeOrderType\":true,\"IsExecutionInstrChange\":true,\"IsAllowUserToTansferTrade\":true,\"IsAllowUserToGenerateSub\":true,\"IsApplyLimitRulesForReplacingStagedOrders\":false,\"IsApplyLimitRulesForReplacingOtherOrders\":false,\"IsApplyLimitRulesForReplacingSubOrders\":false,\"IsAllowRestrictedSecuritiesList\":true,\"IsAllowAllowedSecuritiesList\":false,\"MasterUsersIDs\":null}"
        /// 
        /// </response>
        /// <response code="400">
        /// 
        /// Example Value | Schema :
        ///     
        ///      "CompanyUserId cannot be Blank or 0"
        ///      
        /// </response>
        /// <response code="401">
        /// 
        /// Example Value | Schema :
        ///     
        ///     "Unauthorized"
        ///     
        /// </response>
        [HttpGet]
        [Route(ControllerMethodConstants.CONST_METHOD_GET_COMPANY_TRANSFER_TRADE_RULES)]
        public async Task<IActionResult> GetCompanyTransferTradeRules()
        {

            if (UserDtoObj.CompanyUserId == 0)
                return BadRequest(MessageConstants.MSG_CONST_COMPANYUSERID_CANNOT_BLANK);

            RequestResponseModel requestResponseObj = new(UserDtoObj.CompanyUserId, string.Empty, CorrelationId);
            using (LogKafkaId(requestResponseObj))
            {
                try
                {
                    await _commonDataService.GetCompanyTransferTradeRules(requestResponseObj);
                    return Ok();
                }
                catch (Exception ex)
                {
                    return BadRequest(MessageConstants.MSG_CONST_AN_ERROR_OCCURRED + ex.Message);
                }
            }
        }

