        [HttpGet]
        [Route(ControllerMethodConstants.CONST_METHOD_GET_TRADING_TICKET_CACHE_DATA)]
        public IActionResult GetTradingTicketCacheData()
        {
            try
            {
                _logger.LogInformation(MessageConstants.CONST_LINE_BREAK);
                _logger.LogInformation(MessageConstants.MSG_GET_TT_CACHE_DATA_REQUEST + UserDtoObj.CompanyUserId + DateTime.UtcNow.ToString("dd/MM/yy hh:mm:ss.fff tt"));
                if (UserDtoObj.CompanyUserId == 0)
                    return BadRequest(MessageConstants.MSG_CONST_COMPANYUSERID_CANNOT_BLANK);
                _logger.LogInformation("Executing GetTradingTicketCacheData API....");

                var tradingTicketCache = TradingTicketCache.GetInstance();

                var response = new
                {
                    TradingTicketData = tradingTicketCache.GetTradingTicketData(UserDtoObj.CompanyUserId),
                    BrokerData = tradingTicketCache.GetBrokerData(UserDtoObj.CompanyUserId)
                };
                var data = HelperFunctions.CompressData(JsonConvert.SerializeObject(response));

                _logger.LogInformation(MessageConstants.MSG_GET_TT_CACHE_DATA_RESPONSE + UserDtoObj.CompanyUserId + DateTime.UtcNow.ToString("dd/MM/yy hh:mm:ss.fff tt"));
                _logger.LogInformation(MessageConstants.CONST_LINE_BREAK);

                return Ok(data);
            }
            catch (Exception ex)
            {
                return StatusCode(500, $"Error fetching trading ticket cache data: {ex.Message}");
            }
        }

